import { useMutation } from "@apollo/client";
import { useState } from "react";
import { SAVE_COMPTE } from "../graphql/mutations";
import { GET_ALL_COMPTES } from "../graphql/queries";

function AccountForm() {
  const [formData, setFormData] = useState({
    solde: "",
    type: "COURANT",
    dateCreation: new Date().toISOString().split("T")[0],
  });

  const [saveCompte] = useMutation(SAVE_COMPTE, {
    refetchQueries: [{ query: GET_ALL_COMPTES }],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await saveCompte({
        variables: {
          compte: {
            solde: parseFloat(formData.solde),
            type: formData.type,
            dateCreation: formData.dateCreation,
          },
        },
      });
      setFormData({
        solde: "",
        type: "COURANT",
        dateCreation: new Date().toISOString().split("T")[0],
      });
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  return (
    <div className="bg-white rounded shadow p-4">
      <h2 className="text-xl text-blue-600 font-bold mb-4">Créer un compte</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Solde
          </label>
          <input
            type="number"
            step="0.01"
            required
            value={formData.solde}
            onChange={(e) =>
              setFormData({ ...formData, solde: e.target.value })
            }
            className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500 outline-none"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Type
          </label>
          <select
            required
            value={formData.type}
            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500 outline-none"
          >
            <option value="COURANT">Compte Courant</option>
            <option value="EPARGNE">Compte Épargne</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Date de création
          </label>
          <input
            type="date"
            required
            value={formData.dateCreation}
            onChange={(e) =>
              setFormData({ ...formData, dateCreation: e.target.value })
            }
            className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500 outline-none"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition-colors"
        >
          Créer le compte
        </button>
      </form>
    </div>
  );
}

export default AccountForm;
