import { useQuery } from "@apollo/client";
import { GET_ALL_COMPTES } from "../graphql/queries";

function AccountTable() {
  const { loading, error, data } = useQuery(GET_ALL_COMPTES);

  if (loading) {
    return (
      <div className="w-full h-64 flex items-center justify-center bg-white rounded-lg shadow-md">
        <div className="animate-spin w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="w-full p-4 bg-red-50 border border-red-200 rounded-lg text-red-700">
        Une erreur est survenue lors du chargement des données
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-xl font-bold text-blue-800">Comptes Bancaires</h2>
      </div>
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                ID
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Solde
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Type
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Date Création
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {data.allComptes.map((compte) => (
              <tr key={compte.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-gray-900">
                  {compte.id}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span
                    className={
                      compte.solde >= 0 ? "text-green-600" : "text-red-600"
                    }
                  >
                    {compte.solde.toLocaleString("fr-FR")}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span
                    className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full
                    ${
                      compte.type === "COURANT"
                        ? "bg-yellow-100 text-yellow-800"
                        : "bg-green-100 text-green-800"
                    }`}
                  >
                    {compte.type}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-gray-500">
                  {new Date(compte.dateCreation).toLocaleDateString("fr-FR")}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {data.allComptes.length === 0 && (
          <div className="text-center py-8 text-gray-500">
            Aucun compte disponible
          </div>
        )}
      </div>
    </div>
  );
}

export default AccountTable;
