import { useQuery } from "@apollo/client";
import { GET_TOTAL_SOLDE } from "../graphql/queries";

function SoldeStats() {
  const { loading, error, data } = useQuery(GET_TOTAL_SOLDE, {
    fetchPolicy: "network-only",
    pollInterval: 5000,
  });

  if (loading || error) return null;

  return (
    <div className="max-w-4xl mx-auto mt-8 mb-8 grid grid-cols-1 md:grid-cols-3 gap-4">
      <div className="bg-white p-4 rounded-lg shadow">
        <h3 className="text-gray-500 text-sm">Nombre de comptes</h3>
        <p className="text-2xl font-bold">{data.totalSolde.count}</p>
      </div>
      <div className="bg-white p-4 rounded-lg shadow">
        <h3 className="text-gray-500 text-sm">Solde total</h3>
        <p className="text-2xl font-bold">
          {new Intl.NumberFormat("fr-FR", {
            style: "currency",
            currency: "EUR",
          }).format(data.totalSolde.sum)}
        </p>
      </div>
      <div className="bg-white p-4 rounded-lg shadow">
        <h3 className="text-gray-500 text-sm">Solde moyen</h3>
        <p className="text-2xl font-bold">
          {new Intl.NumberFormat("fr-FR", {
            style: "currency",
            currency: "EUR",
          }).format(data.totalSolde.average)}
        </p>
      </div>
    </div>
  );
}

export default SoldeStats;
