import { gql } from "@apollo/client";

export const SAVE_COMPTE = gql`
  mutation SaveCompte($compte: CompteRequest!) {
    saveCompte(compte: $compte) {
      id
      solde
      dateCreation
      type
    }
  }
`;

export const DELETE_COMPTE = gql`
  query DeleteCompte($id: ID) {
    deleteCompte(id: $id)
  }
`;
