import { useQuery } from "@apollo/client";
import { GET_ALL_COMPTES } from "../graphql/queries";

function AccountTable() {
  const { loading, error, data } = useQuery(GET_ALL_COMPTES, {
    fetchPolicy: "network-only",
    onError: (error) => {
      console.error("Erreur lors du chargement des comptes:", error);
    },
  });

  if (loading) {
    return (
      <div className="flex justify-center items-center h-32">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-500 text-center p-4">
        Erreur lors du chargement des comptes: {error.message}
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              ID
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Solde
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Type
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Date de création
            </th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {data.allComptes.map((compte) => (
            <tr key={compte.id}>
              <td className="px-6 py-4 whitespace-nowrap">{compte.id}</td>
              <td className="px-6 py-4 whitespace-nowrap">
                {new Intl.NumberFormat("fr-FR", {
                  style: "currency",
                  currency: "EUR",
                }).format(compte.solde)}
              </td>
              <td className="px-6 py-4 whitespace-nowrap">
                {compte.type === "COURANT"
                  ? "Compte Courant"
                  : "Compte Épargne"}
              </td>
              <td className="px-6 py-4 whitespace-nowrap">
                {new Date(compte.dateCreation).toLocaleDateString("fr-FR")}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AccountTable;
