// src/components/AccountForm.jsx
import { useMutation } from "@apollo/client";
import { useState } from "react";
import { SAVE_COMPTE } from "../graphql/mutations";
import { GET_ALL_COMPTES } from "../graphql/queries";

function AccountForm() {
  const [formData, setFormData] = useState({
    solde: "",
    type: "COURANT",
    dateCreation: new Date().toISOString().split("T")[0],
  });

  const [saveCompte, { loading }] = useMutation(SAVE_COMPTE, {
    refetchQueries: [{ query: GET_ALL_COMPTES }],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const date = new Date(formData.dateCreation);
      const jour = String(date.getDate()).padStart(2, "0");
      const mois = String(date.getMonth() + 1).padStart(2, "0");
      const annee = date.getFullYear();
      const formattedDate = `${jour}/${mois}/${annee}`;

      await saveCompte({
        variables: {
          compte: {
            solde: parseFloat(formData.solde),
            type: formData.type,
            dateCreation: formattedDate,
          },
        },
      });

      setFormData({
        solde: "",
        type: "COURANT",
        dateCreation: new Date().toISOString().split("T")[0],
      });
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  return (
    <div className="bg-[#5B21B6] rounded-lg p-6 shadow-lg">
      <h2 className="text-white text-xl font-semibold mb-6 text-center">
        Nouveau Compte
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="relative">
          <input
            type="number"
            step="0.01"
            required
            value={formData.solde}
            onChange={(e) =>
              setFormData({ ...formData, solde: e.target.value })
            }
            placeholder="Solde initial"
            className="w-full bg-[#6D28D9] text-white placeholder-gray-300 px-4 py-3 rounded-md focus:outline-none"
          />
          <span className="absolute right-4 top-3 text-gray-300">DH</span>
        </div>

        <div>
          <select
            required
            value={formData.type}
            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            className="w-full bg-[#6D28D9] text-white px-4 py-3 rounded-md focus:outline-none appearance-none"
          >
            <option value="COURANT">Compte Courant</option>
            <option value="EPARGNE">Compte Épargne</option>
          </select>
        </div>

        <div>
          <input
            type="date"
            required
            value={formData.dateCreation}
            onChange={(e) =>
              setFormData({ ...formData, dateCreation: e.target.value })
            }
            className="w-full bg-[#6D28D9] text-white px-4 py-3 rounded-md focus:outline-none"
            max={new Date().toISOString().split("T")[0]}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className="w-full bg-[#9333EA] hover:bg-[#8B5CF6] text-white py-3 rounded-md transition-colors"
        >
          {loading ? "Création..." : "Créer le compte"}
        </button>
      </form>
    </div>
  );
}

export default AccountForm;
