// src/components/AccountForm.jsx
import { useMutation } from "@apollo/client";
import { useState } from "react";
import { SAVE_COMPTE } from "../graphql/mutations";
import { GET_ALL_COMPTES } from "../graphql/queries";

function AccountForm() {
  const [formData, setFormData] = useState({
    solde: "",
    type: "",
    dateCreation: "",
  });

  const [saveCompte, { loading }] = useMutation(SAVE_COMPTE, {
    refetchQueries: [{ query: GET_ALL_COMPTES }],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const date = new Date(formData.dateCreation);
      const jour = String(date.getDate()).padStart(2, "0");
      const mois = String(date.getMonth() + 1).padStart(2, "0");
      const annee = date.getFullYear();
      const formattedDate = `${jour}/${mois}/${annee}`;

      await saveCompte({
        variables: {
          compte: {
            solde: parseFloat(formData.solde),
            type: formData.type,
            dateCreation: formattedDate,
          },
        },
      });

      setFormData({
        solde: "",
        type: "",
        dateCreation: "",
      });
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  return (
    <div className="bg-gray-900 rounded-xl shadow-xl p-6">
      <h2 className="text-2xl text-white font-bold mb-6">Créer un compte</h2>
      <form onSubmit={handleSubmit} className="space-y-5">
        <div>
          <div className="flex justify-between items-center mb-2">
            <label className="text-sm text-gray-300">Solde initial</label>
            <span className="text-sm text-gray-300">DH</span>
          </div>
          <input
            type="number"
            step="0.01"
            required
            value={formData.solde}
            onChange={(e) =>
              setFormData({ ...formData, solde: e.target.value })
            }
            className="w-full bg-gray-800 text-white px-4 py-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
            placeholder="Entrez le solde"
          />
        </div>

        <div>
          <label className="block text-sm text-gray-300 mb-2">
            Type de compte
          </label>
          <select
            required
            value={formData.type}
            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            className="w-full bg-gray-800 text-white px-4 py-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          >
            <option value="">Sélectionnez un type</option>
            <option value="COURANT">Compte Courant</option>
            <option value="EPARGNE">Compte Épargne</option>
          </select>
        </div>

        <div>
          <label className="block text-sm text-gray-300 mb-2">
            Date de création
          </label>
          <input
            type="date"
            required
            value={formData.dateCreation}
            onChange={(e) =>
              setFormData({ ...formData, dateCreation: e.target.value })
            }
            className="w-full bg-gray-800 text-white px-4 py-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className={`w-full mt-6 px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 
                   text-white font-medium rounded-lg 
                   hover:from-blue-600 hover:to-blue-700 
                   focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 
                   focus:ring-offset-gray-900 transition-all
                   ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
        >
          {loading ? (
            <div className="flex items-center justify-center">
              <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
              Création en cours...
            </div>
          ) : (
            "Créer le compte"
          )}
        </button>
      </form>
    </div>
  );
}

export default AccountForm;
