import { gql } from "@apollo/client";

export const GET_ALL_COMPTES = gql`
  query GetAllComptes {
    allComptes {
      id
      solde
      dateCreation
      type
    }
  }
`;

export const GET_COMPTE_BY_ID = gql`
  query GetCompteById($id: ID) {
    compteById(id: $id) {
      id
      solde
      dateCreation
      type
    }
  }
`;

export const GET_TOTAL_SOLDE = gql`
  query GetTotalSolde {
    totalSolde {
      count
      sum
      average
    }
  }
`;
