import { useMutation } from "@apollo/client";
import { useState } from "react";
import { SAVE_COMPTE } from "../graphql/mutations";
import { GET_ALL_COMPTES, GET_TOTAL_SOLDE } from "../graphql/queries";

function AccountForm() {
  const [formData, setFormData] = useState({
    solde: "",
    type: "COURANT",
    dateCreation: new Date().toISOString().split("T")[0],
  });

  const [saveCompte, { loading: savingCompte }] = useMutation(SAVE_COMPTE, {
    refetchQueries: [{ query: GET_ALL_COMPTES }, { query: GET_TOTAL_SOLDE }],
    onError: (error) => {
      console.error("Erreur lors de la création:", error);
    },
    onCompleted: () => {
      setFormData({
        solde: "",
        type: "COURANT",
        dateCreation: new Date().toISOString().split("T")[0],
      });
    },
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Formatter la date en format "yyyy-MM-dd"
      const date = new Date(formData.dateCreation);
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, "0");
      const day = String(date.getDate()).padStart(2, "0");
      const formattedDate = `${year}-${month}-${day}`;

      await saveCompte({
        variables: {
          compte: {
            solde: parseFloat(formData.solde),
            type: formData.type,
            dateCreation: formattedDate,
          },
        },
      });
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md mb-8">
      <h2 className="text-xl font-bold mb-4">Créer un nouveau compte</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Solde initial
          </label>
          <input
            type="number"
            step="0.01"
            name="solde"
            value={formData.solde}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
            required
            min="0"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Type de compte
          </label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
            required
          >
            <option value="COURANT">Compte Courant</option>
            <option value="EPARGNE">Compte Épargne</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Date de création
          </label>
          <input
            type="date"
            name="dateCreation"
            value={formData.dateCreation}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2"
            required
          />
        </div>

        <button
          type="submit"
          disabled={savingCompte}
          className={`w-full ${
            savingCompte ? "bg-blue-300" : "bg-blue-500 hover:bg-blue-600"
          } text-white py-2 px-4 rounded-md transition duration-200`}
        >
          {savingCompte ? "Création en cours..." : "Créer le compte"}
        </button>
      </form>
    </div>
  );
}

export default AccountForm;
